@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground font-sans antialiased;
    font-feature-settings: "cv02", "cv03", "cv04", "cv11";
    transition: background-color 0.3s ease, color 0.3s ease;
  }

  /* Smooth theme transitions */
  * {
    transition: background-color 0.3s ease, border-color 0.3s ease, color 0.3s ease;
  }
}

@layer components {
  .glass {
    @apply backdrop-blur-xl rounded-2xl;
    background-color: hsl(var(--glass));
    border: 1px solid hsl(var(--glass-border));
    box-shadow: var(--shadow-glass);
  }

  .glass-card {
    @apply glass p-8;
  }

  .glass-subtle {
    @apply backdrop-blur-lg rounded-xl;
    background-color: hsl(var(--glass-subtle));
    border: 1px solid hsl(var(--glass-border));
    box-shadow: 0 4px 24px hsl(var(--glass-shadow) / 0.1);
  }

  .hero-glow {
    background: var(--gradient-hero);
  }

  .mesh-bg {
    background: var(--gradient-mesh);
  }

  .aurora-background {
    will-change: transform, opacity;
    backface-visibility: hidden;
    transform: translateZ(0);
  }

  /* Professional Aurora fade effects */
  .aurora-fade-bottom {
    mask-image: var(--fade-gradient-bottom);
    -webkit-mask-image: var(--fade-gradient-bottom);
  }

  .aurora-fade-top {
    mask-image: var(--fade-gradient-top);
    -webkit-mask-image: var(--fade-gradient-top);
  }

  .aurora-fade-both {
    mask-image: var(--fade-gradient-both);
    -webkit-mask-image: var(--fade-gradient-both);
  }

  /* Ensure aurora renders behind all content */
  .aurora-bg {
    isolation: isolate;
  }

  /* Enhance glass effect to work beautifully with aurora */
  .glass {
    backdrop-filter: blur(24px) saturate(180%);
  }

  .glass-card {
    backdrop-filter: blur(20px) saturate(160%);
  }

  .glass-subtle {
    backdrop-filter: blur(16px) saturate(140%);
  }

  .text-gradient {
    background: var(--gradient-primary);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .glow-on-hover {
    transition: all var(--transition-smooth);
  }

  .glow-on-hover:hover {
    box-shadow: var(--shadow-glow);
    transform: translateY(-2px);
  }

  .premium-glow {
    box-shadow: var(--shadow-premium);
  }

  .typewriter {
    overflow: hidden;
    border-right: 2px solid hsl(var(--primary));
    white-space: nowrap;
    animation: typewriter 3s steps(40, end), blink-caret 0.75s step-end infinite;
  }

  .fade-in-viewport {
    opacity: 0;
    transform: translateY(30px);
    transition: all 0.6s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .fade-in-viewport.in-view {
    opacity: 1;
    transform: translateY(0);
  }

  .floating {
    animation: floating 6s ease-in-out infinite;
  }

  .pulse-glow {
    animation: pulse-glow 2s ease-in-out infinite;
  }

  /* Magic UI Components */
  .aurora-bg {
    position: fixed;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    pointer-events: none;
    z-index: 0;
    background: linear-gradient(45deg, 
      hsla(var(--aurora-primary), 0.1), 
      hsla(var(--aurora-secondary), 0.1), 
      hsla(var(--aurora-tertiary), 0.1));
    animation: aurora 15s ease-in-out infinite;
  }

  .magic-bento {
    @apply rounded-2xl overflow-hidden relative;
    background: rgba(255, 255, 255, 0.02);
    border: 1px solid rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(20px);
    transition: all 0.3s ease;
  }

  .magic-bento:hover {
    transform: translateY(-4px) scale(1.02);
    box-shadow: 0 20px 40px rgba(99, 102, 241, 0.15);
  }

  .tilted-card {
    perspective: 1000px;
    transition: transform 0.3s ease;
  }

  .tilted-card:hover .card-inner {
    transform: rotateY(12deg) rotateX(5deg);
  }

  .card-inner {
    transform-style: preserve-3d;
    transition: transform 0.3s ease;
  }

  .dock-container {
    @apply fixed bottom-6 left-1/2 transform -translate-x-1/2 z-50;
  }

  .dock {
    @apply flex items-end space-x-2 p-2 rounded-2xl backdrop-blur-xl bg-white/10 border border-white/20;
  }

  .dock-item {
    @apply w-12 h-12 rounded-xl bg-white/10 flex items-center justify-center transition-all duration-300 hover:w-16 hover:h-16 cursor-pointer;
  }
}

@layer utilities {
  .animate-typewriter {
    animation: typewriter 3s steps(40, end), blink-caret 0.75s step-end infinite;
  }

  .animate-fade-in-up {
    animation: fadeInUp 0.6s ease-out;
  }

  .animate-fade-in-down {
    animation: fadeInDown 0.6s ease-out;
  }

  .animate-scale-in {
    animation: scaleIn 0.4s ease-out;
  }

  .animate-slide-in-left {
    animation: slideInLeft 0.6s ease-out;
  }

  .animate-slide-in-right {
    animation: slideInRight 0.6s ease-out;
  }
}

@keyframes typewriter {
  from { width: 0; }
  to { width: 100%; }
}

@keyframes blink-caret {
  from, to { border-color: transparent; }
  50% { border-color: hsl(var(--primary)); }
}

@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes fadeInDown {
  from {
    opacity: 0;
    transform: translateY(-30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes scaleIn {
  from {
    opacity: 0;
    transform: scale(0.9);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes slideInLeft {
  from {
    opacity: 0;
    transform: translateX(-30px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes slideInRight {
  from {
    opacity: 0;
    transform: translateX(30px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes floating {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
}

@keyframes pulse-glow {
  0%, 100% { 
    box-shadow: 0 0 20px hsl(var(--primary) / 0.3);
  }
  50% { 
    box-shadow: 0 0 40px hsl(var(--primary) / 0.6);
  }
}

@keyframes aurora {
  0%, 100% {
    transform: rotate(0deg) scale(1);
    opacity: 0.7;
  }
  33% {
    transform: rotate(120deg) scale(1.1);
    opacity: 0.8;
  }
  66% {
    transform: rotate(240deg) scale(0.9);
    opacity: 0.6;
  }
}

@keyframes tilt {
  0%, 100% {
    transform: rotateY(0deg) rotateX(0deg);
  }
  25% {
    transform: rotateY(5deg) rotateX(2deg);
  }
  75% {
    transform: rotateY(-5deg) rotateX(-2deg);
  }
}

@keyframes magnetism {
  0% {
    transform: scale(1);
  }
  50% {
    transform: scale(1.05);
  }
  100% {
    transform: scale(1);
  }
}

