#!/usr/bin/env python3\n\"\"\"StackStage FastAPI Backend Startup Script\"\"\"\n\nimport uvicorn\nimport os\nfrom dotenv import load_dotenv\n\n# Load environment variables\nload_dotenv()\n\nif __name__ == \"__main__\":\n    # Configuration\n    host = \"0.0.0.0\"\n    port = int(os.getenv(\"PORT\", 8000))\n    debug = os.getenv(\"NODE_ENV\") != \"production\"\n    \n    print(f\"ğŸš€ Starting StackStage FastAPI Backend...\")\n    print(f\"ğŸ“ Host: {host}\")\n    print(f\"ğŸ”Œ Port: {port}\")\n    print(f\"ğŸ› Debug mode: {debug}\")\n    print(f\"ğŸŒ Environment: {os.getenv('NODE_ENV', 'development')}\")\n    \n    # Start the server\n    uvicorn.run(\n        \"main:app\",\n        host=host,\n        port=port,\n        reload=debug,\n        log_level=\"info\" if debug else \"warning\"\n    )\n